var SCRIPT_RESOURCE_ID = "res-script";
var REQUIRE_RESOURCE_JS_ID = "res-require-js";

var getScriptRes = function() {
	var scriptCode = "lemur.echo('<script>');lemur.runRes('" + SCRIPT_RESOURCE_ID + "');lemur.echo('</script>');";
	return scriptCode;
}

var getScriptJsRes = function() {
	var scriptCode = "lemur.runRes('" + REQUIRE_RESOURCE_JS_ID + "');";
	return scriptCode;
}

module.exports = {
	tagToken: {
		tagName: "body",
		tagType: "block"
	},
	process: function(tagNode) {},
	interpret: function(token, childCodes) {
		var nextCode = childCodes && childCodes.join("");
		nextCode = nextCode || "";
		var scriptCode = getScriptRes();
		var scriptJsCode = getScriptJsRes();
		return "lemur.echo('<body>');" + nextCode + scriptJsCode + scriptCode + ";lemur.echo('</body>');";
	}
}