var REQUIRE_RESOURCE_CSS_ID = "res-require-css";

var getLinkCssRes = function() {
	var scriptCode = "lemur.runRes('" + REQUIRE_RESOURCE_CSS_ID + "');";
	return scriptCode;
}

module.exports = {
	tagToken: {
		tagName: "head",
		tagType: "block"
	},
	process: function(tagNode) {},
	interpret: function(token, childCodes) {
		var nextCode = childCodes.join("");
		var cssCode = getLinkCssRes();
		return "lemur.echo('<head>');" + nextCode + cssCode + "lemur.echo('</head>');";
	}
}