/**
 * html tag:
 * controll document start and end.
 */

module.exports = {
	tagToken: {
		tagName: "html",
		tagType: "block"
	},
	process: function(tagNode) {},
	interpret: function(token, childCodes) {
		var nextCode = childCodes && childCodes.join("");
		nextCode = nextCode || "";
		return "lemur.echo('<!DOCTYPE html><html>');" + nextCode + ";lemur.echo('</html>');";
	}
}