/**
 * <#script#> js code <#/script#>
 * Collect all these kind of js codes, expected to execute them at the last.
 */


var RESOURCE_ID = "res-script";

module.exports = {
	tagToken: {
		tagName: "script",
		tagType: "block"
	},
	process: function(tagNode) {},
	interpret: function(token, childCodes) {
		var nextCode = childCodes.join("");
		this.addGlobalCode("lemur.collect('" + RESOURCE_ID + "' , function(){" + nextCode + "},'" + this.funName + "');");
	}
}