var Block = require("../lib/interpreter/lemurInterface/block.js");
var assert = require("assert");

describe("test Block class", function() {
	it('appendChild', function() {
		var block1 = new Block();
		var block2 = new Block();
		var block3 = new Block();
		block1.appendChild(block2);
		block1.appendChild(block3);
		assert.equal(block1.children[0], block2);
		assert.equal(block1.children[1], block3);
	});

	it('isAncestor & isDescendant', function() {
		var block1 = new Block();
		
		var block2 = new Block();
		var block3 = new Block();
		
		var block4 = new Block();
		block1.appendChild(block2);
		block1.appendChild(block3);

		block2.appendChild(block4);
		
		assert.equal(true, block1.isAncestor(block2));
		assert.equal(true, block1.isAncestor(block3));
		assert.equal(true, block1.isAncestor(block4));

		assert.equal(true, block2.isDescendant(block1));
		assert.equal(true, block3.isDescendant(block1));
		assert.equal(true, block4.isDescendant(block1));
		assert.equal(true, block4.isDescendant(block2));
	});
});