var forseti = require("forseti");
var maker = forseti.createMarker(true);
var fs = require("fs");

maker.addRule("iaef", { // shortcut of " is an exist file or directory"
	"check": function(json, propName, extra) {
		var filePath = json[propName];
		if (filePath) {
			return fs.existsSync(filePath);
		}
		return true;
	}
});

module.exports = maker;