var printLog = null;
var printErrorLog = null;

var logConf = {
	log: function(info, color) {
		color = color || "green";
		var content = typeof info === "string" ? info[color] : JSON.stringify(info)[color];
		console.log(content);
		printLog && printLog(content);
	},
	logError: function(info, color) {
		color = color || "red";
		var content = typeof info === "string" ? info[color] : JSON.stringify(info)[color];
		console.log(content);
		printErrorLog && printErrorLog(content);
	}
}

logConf.printLog = function(logFunc) {
	if (typeof logFunc === "function") {
		printLog = logFunc;
	}
}
logConf.printErrorLog = function(logErrorFunc) {
	if (typeof logErrorFunc === "function") {
		printErrorLog = logErrorFunc;
	}
}

module.exports = logConf;