var middlewareManager = (function() {
	var load = function(app, middlewareConfig) {
		middlewareConfig.forEach(function(elem) {
			var filePath = elem.filePath;
			importMiddleware(app, elem);
		});
	}
	var importMiddleware = function(app, elem) {
		var filePath = elem.filePath;
		var middleAction = require(filePath);
		middleAction && middleAction(app);
	}

	return {
		load: load
	}
})();

module.exports = middlewareManager;