var path = require('path');
var express = require("express");

module.exports = function(app) {
	var appConf = app.tarsier.appConf;
	for (var appName in appConf.app) {
		var modulePath = appConf.app[appName].modulePath;
		var staticResPath = path.join(modulePath, "static");
		if (appConf.app.static) {
			staticResPath = appConf.app.static;
		}
		// static resources
		app.use("/static/" + appName, express.static(staticResPath));
	}
}